mapper.select:
SELECT 	count(*) + 1
FROM    	USERS
WHERE 	dept_num = #{dept_num}

SELECT 	*
FROM 		DEPARTMENT
WHERE 	dept_num = #{dept_num}

mapper.update:
UPDATE DEPARTMENT 
SET dept_name = #{dept_name}, dept_tel = #{dept_tel}
WHERE dept_num = #{dept_num}

mapper.select:
SELECT *
FROM DEPARTMENT
WHERE dept_name LIKE #{dept_name}

SELECT *
FROM PRODUCT
WHERE pro_name LIKE #{pro_name}

SELECT *
FROM SUPPLIER
WHERE supp_name LIKE #{supp_name}

SELECT *
FROM SUPPLIER
WHERE supp_id = #{supp_id}

SELECT *
FROM SUPPLIER
WHERE supp_id = #{supp_id}

SELECT *
FROM USERS
WHERE user_num 	= #{user_num}

SELECT 	dept_name
FROM 		DEPARTMENT
WHERE 	dept_num = #{dept_num}

SELECT *
FROM DEPARTMENT
WHERE dept_num = #{dept_num}
